// Assume que 'output' é a variável que contém o texto recebido pelo nó anterior.
const textInput = $input.first().json.output || ""; 

// Substitui todos os '**' por '*' no texto
const formattedText = textInput.replace(/\*\*/g, '*');

// Função para verificar se uma string é numérica
function isNumeric(str) {
  return !isNaN(str) && !isNaN(parseFloat(str));
}

// Lista de abreviações comuns
let abbreviations = ["dr.", "sr.", "sra.", "ex.", "etc.", "mr.", "mrs.", "ms.", "prof.", "p.e.", "i.e.", "vs."];

// Tenta decodificar o texto, tratando possíveis erros de decodificação
let str = "";
try {
  str = decodeURIComponent(formattedText).replace(/"/g, "'");
} catch (e) {
  console.error("Erro na decodificação do texto:", e.message);
  str = formattedText;
}

// Encontra os índices para divisão do texto com base em pontuações e outras regras
let splitIndices = [];
for (let i = 0; i < str.length; i++) {
  if (['.', '!', '?', ';'].includes(str[i])) {
    // Ignora pontos duplos
    if (i < str.length - 1 && str[i] === str[i + 1]) continue;
    // Ignora pontos em números (ex: 3.14)
    if (i > 0 && i < str.length - 1 && isNumeric(str[i - 1]) && isNumeric(str[i + 1])) continue;
    // Ignora abreviações
    let isAbbreviation = abbreviations.some(abbr =>
      str.slice(i - abbr.length + 1, i + 1).toLowerCase() === abbr
    );
    if (isAbbreviation) continue;
    // Exige espaço após a pontuação
    if (i < str.length - 1 && ![' ', '\n'].includes(str[i + 1])) continue;
    splitIndices.push(i);
  }
}

// Divide o texto nos índices identificados
let strArray = [];
let lastIdx = 0;
splitIndices.forEach(idx => {
  const part = str.slice(lastIdx, idx + 1).trim();
  if (part) strArray.push({ part });
  lastIdx = idx + 1;
});
// Adiciona a última parte
const lastPart = str.slice(lastIdx).trim();
if (lastPart) strArray.push({ part: lastPart });

// Retorna no formato esperado pelo n8n
return strArray.map(item => ({ json: item }));

