// 1) Pega o objeto inteiro que chegou no webhook (ou no nó anterior)
const data = $input.first().json;

// 2) Extrai (e “parseia”, se for string) o array de números
let contatosRaw = data.numero; // ou "data.contatos", caso o campo se chame assim
let contatos;
try {
  contatos = JSON.parse(contatosRaw);
} catch (e) {
  contatos = contatosRaw;
}

// 3) Para cada número, criamos um clone de `data` e sobrescrevemos só o campo "numero"
const outputs = [];
for (let i = 0; i < contatos.length; i++) {
  // clone profundo do objeto original (para não modificar o data “in-place”)
  const item = JSON.parse(JSON.stringify(data));
  item.numero = contatos[i]; // agora, em vez de array, será só uma string

  outputs.push({ json: item });
}

return outputs;

