// Assume que 'textInput' √© a vari√°vel que cont√©m o texto recebido pelo n√≥ anterior.
const textInput = $input.all()[0].json.text; // Ajuste este caminho conforme necess√°rio

// Substitui todos os '**' por '*' no texto
const formattedText = textInput.replace(/\*\*/g, '*');

// Fun√ß√£o para verificar se uma string √© num√©rica
function isNumeric(str) {
  return !isNaN(str) && !isNaN(parseFloat(str));
}

// Lista de abrevia√ß√µes comuns
let abbreviations = ["dr.", "sr.", "sra.", "ex.", "etc.", "mr.", "mrs.", "ms.", "prof.", "p.e.", "i.e.", "vs."];

// Tenta decodificar o texto, tratando poss√≠veis erros de decodifica√ß√£o
let str = "";
if (formattedText == null) {
  str = $node["Busca contato no CHATWOOT"].json["text"];
} else {
  try {
    let textoDecodificado = decodeURIComponent(formattedText).replace(/"/g, "'");
    str = textoDecodificado;
  } catch (e) {
    console.error("Erro na decodifica√ß√£o do texto:", e.message);
    str = formattedText;
  }
}

// Encontra os √≠ndices para divis√£o do texto com base em pontua√ß√µes e outras regras
let splitIndices = [];
for (let i = 0; i < str.length; i++) {
  if (['.', '!', '?', ';'].includes(str[i])) {
    if (i < str.length - 1 && str[i] === str[i + 1]) {
      continue;
    }
    if (i > 0 && i < str.length - 1 && isNumeric(str[i - 1]) && isNumeric(str[i + 1])) {
      continue;
    }
    let isAbbreviation = false;
    for (let abbr of abbreviations) {
      if (str.slice(i - abbr.length + 1, i + 1).toLowerCase() === abbr) {
        isAbbreviation = true;
        break;
      }
    }
    if (isAbbreviation) {
      continue;
    }
    if (i < str.length - 1 && str[i + 1] !== ' ' && str[i + 1] !== '\n') {
      continue;
    }
    splitIndices.push(i);
  }
}

// Divide o texto nos √≠ndices identificados
let strArray = [];
let lastIdx = 0;
splitIndices.forEach((idx) => {
  const textPart = str.slice(lastIdx, idx + 1).trim();
  strArray.push({ part: textPart });
  lastIdx = idx + 1;
});
strArray.push({ part: str.slice(lastIdx).trim() });

return strArray.map(part => {
  return { json: part };
});
