// Assume que 'output' √© a vari√°vel que cont√©m o texto recebido pelo n√≥ anterior.
const textInput = $input.first().json.output || ""; 

// Substitui todos os '**' por '*' no texto
const formattedText = textInput.replace(/\*\*/g, '*');

// Fun√ß√£o para verificar se uma string √© num√©rica
function isNumeric(str) {
  return !isNaN(str) && !isNaN(parseFloat(str));
}

// Lista de abrevia√ß√µes comuns
let abbreviations = ["dr.", "sr.", "sra.", "ex.", "etc.", "mr.", "mrs.", "ms.", "prof.", "p.e.", "i.e.", "vs."];

// Tenta decodificar o texto, tratando poss√≠veis erros de decodifica√ß√£o
let str = "";
try {
  str = decodeURIComponent(formattedText).replace(/"/g, "'");
} catch (e) {
  console.error("Erro na decodifica√ß√£o do texto:", e.message);
  str = formattedText;
}

// Encontra os √≠ndices para divis√£o do texto com base em pontua√ß√µes e outras regras
let splitIndices = [];
for (let i = 0; i < str.length; i++) {
  if (['.', '!', '?', ';'].includes(str[i])) {
    // Ignora pontos duplos
    if (i < str.length - 1 && str[i] === str[i + 1]) continue;
    // Ignora pontos em n√∫meros (ex: 3.14)
    if (i > 0 && i < str.length - 1 && isNumeric(str[i - 1]) && isNumeric(str[i + 1])) continue;
    // Ignora abrevia√ß√µes
    let isAbbreviation = abbreviations.some(abbr =>
      str.slice(i - abbr.length + 1, i + 1).toLowerCase() === abbr
    );
    if (isAbbreviation) continue;
    // Exige espa√ßo ap√≥s a pontua√ß√£o
    if (i < str.length - 1 && ![' ', '\n'].includes(str[i + 1])) continue;
    splitIndices.push(i);
  }
}

// Divide o texto nos √≠ndices identificados
let strArray = [];
let lastIdx = 0;
splitIndices.forEach(idx => {
  const part = str.slice(lastIdx, idx + 1).trim();
  if (part) strArray.push({ part });
  lastIdx = idx + 1;
});
// Adiciona a √∫ltima parte
const lastPart = str.slice(lastIdx).trim();
if (lastPart) strArray.push({ part: lastPart });

// Retorna no formato esperado pelo n8n
return strArray.map(item => ({ json: item }));

// Fun√ß√£o para formatar telefone corretamente
function formatarTelefone(phone_number) {
    let whatsapp = phone_number.replace(/\D/g, ''); // Remove tudo que n√£o for n√∫mero
    const ddi = "55"; // O DDI sempre ser√° 55
    let ddd;

    // Remove o DDI se come√ßar com 55 (evita duplica√ß√£o)
    if (whatsapp.startsWith("55")) {
        whatsapp = whatsapp.slice(2);
    }

    // Extraindo o DDD
    if (whatsapp.length >= 10) {
        ddd = whatsapp.slice(0, 2);
        whatsapp = whatsapp.slice(2);
    } else {
        ddd = '00';
    }

    // Adicionando o "9" para n√∫meros m√≥veis (todos os DDDs no Brasil seguem essa regra)
    if (whatsapp.length === 8) {
        whatsapp = '9' + whatsapp;
    }

    return ddi + ddd + whatsapp; // Retorna SEM o "+"
}

// Obtendo o telefone do novo caminho no JSON do N8N
const telefoneOriginal = $input.first().json.body.conversation.messages[0].sender.phone_number || "";

// Verificando se h√° telefone v√°lido
if (telefoneOriginal) {
    return [{
        telefone_formatado: formatarTelefone(telefoneOriginal)
    }];
} else {
    return [{
        error: "Nenhum telefone v√°lido encontrado"
    }];
}

