  // Extrai e limpa o tipo de f√≠sico (remove '*' e ponto final)
  let tipoFisico = parts[1] ? parts[1].trim() : '';
  tipoFisico = tipoFisico
    .replace(/^\*+/, '')      // remove * no in√≠cio
