// Obt√©m o objeto de changed_attributes desejado
const changedAttr = $('Webhook').first().json.body.changed_attributes[1];

// Verifica se o objeto e a propriedade cached_label_list existem
if (!changedAttr || !changedAttr.cached_label_list) {
  return [
    {
      json: {
        message: "Webhook sem cached_label_list. Prosseguindo normalmente."
      }
    }
  ];
}

// Converte os valores para string vazia se estiverem indefinidos
const previousValue = changedAttr.cached_label_list.previous_value || "";
const currentValue  = changedAttr.cached_label_list.current_value  || "";

// Se ambos os valores estiverem vazios, significa que n√£o h√° tag atribu√≠da
if (!previousValue && !currentValue) {
  return [
    {
      json: {
        message: "Nenhuma tag atribu√≠da. Prosseguindo normalmente."
      }
    }
  ];
}

// Converte as strings em arrays, removendo espa√ßos e filtrando entradas vazias
const previousArray = previousValue.split(',').map(tag => tag.trim()).filter(tag => tag !== "");
const currentArray  = currentValue.split(',').map(tag => tag.trim()).filter(tag => tag !== "");

// Filtra as tags que est√£o no currentArray e n√£o est√£o no previousArray
const exclusiveTags = currentArray.filter(tag => !previousArray.includes(tag));

// Se nenhuma tag exclusiva for encontrada, retorna mensagem informando
if (exclusiveTags.length === 0) {
  return [
    {
      json: {
        message: "Nenhuma tag exclusiva encontrada."
      }
    }
  ];
}

// Retorna um array de objetos, um para cada tag exclusiva
return exclusiveTags.map(tag => ({
  json: {
    exclusive_tag: tag
  }
}));

